package dowhileloop;

public class Dowhiletest1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;

        do {
            System.out.println(i);
            i = i + 1;
        } while (i <= 10); // Note: The semicolon is required here!

        System.out.println("Loop finished.");

	}

}
